mapper.select:
SELECT b.pkid, b.logo_pkid, b.`name`, b.bussiness_type, b.bussiness_pkid, b.forward_type, b.url, r.url AS http_url FROM banner b LEFT JOIN resource r ON b.logo_pkid = r.pkid WHERE b.bussiness_type = #{bussinessType}
AND b.pkid IN ( SELECT banner_pkid FROM loan_data )
AND b.pkid IN ( SELECT banner_pkid FROM credit_card )

SELECT b.pkid, b.logo_pkid, b.`name`, b.bussiness_type, b.bussiness_pkid, b.forward_type, b.url, r.url AS http_url FROM banner b LEFT JOIN resource r ON b.logo_pkid = r.pkid WHERE b.bussiness_type = #{bussinessType}
AND b.pkid NOT IN ( SELECT banner_pkid FROM loan_data )
AND b.pkid NOT IN ( SELECT banner_pkid FROM credit_card )
